-- Mock data for Aeron Image Manager testing
-- Based on production database structure

-- Create schema if not exists
CREATE SCHEMA IF NOT EXISTS aeron;

-- Create artist table
CREATE TABLE IF NOT EXISTS aeron.artist (
    artistid UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    artist VARCHAR(255) NOT NULL UNIQUE,
    picture BYTEA
);

-- Create track table
CREATE TABLE IF NOT EXISTS aeron.track (
    titleid UUID PRIMARY KEY DEFAULT gen_random_uuid(),
    tracktitle VARCHAR(255) NOT NULL,
    artist VARCHAR(255),
    picture BYTEA
);

