-- Add some statistics about the test data
DO $$
DECLARE
    artist_count INTEGER;
    track_count INTEGER;
    artists_with_images INTEGER;
    tracks_with_images INTEGER;
BEGIN
    SELECT COUNT(*) INTO artist_count FROM aeron.artist;
    SELECT COUNT(*) INTO track_count FROM aeron.track;
    SELECT COUNT(*) INTO artists_with_images FROM aeron.artist WHERE picture IS NOT NULL;
    SELECT COUNT(*) INTO tracks_with_images FROM aeron.track WHERE picture IS NOT NULL;
    
    RAISE NOTICE 'Test data loaded successfully:';
    RAISE NOTICE '  Total artists: %', artist_count;
    RAISE NOTICE '  Artists with images: %', artists_with_images;
    RAISE NOTICE '  Total tracks: %', track_count;
    RAISE NOTICE '  Tracks with images: %', tracks_with_images;
END $$;